sys_script_include.script:
var serverSubmission = Class.create();
serverSubmission.prototype = {
    initialize: function() {
    },
	//this fucntion return whether current logged on user has in-progress submission or not 
	checkUserHasNoInProgressSubmission: function(){
		var grSubmission = new GlideRecord('x_560219_se_submission');
		grSubmission.addQuery('sys_created_by', gs.getUserName());
		grSubmission.query();
		while(grSubmission.next()){
			if(parseInt(grSubmission.se_submission_state) >= 1 && parseInt(grSubmission.se_submission_state) < 3){
				return false;
			}
		}
		return true;
	},
    type: 'serverSubmission'
};
