sys_security_acl.script:
answer = true;
var grSubmission = new GlideRecord('x_560219_se_submission');
grSubmission.addQuery('sys_id', current.se_cert_acquisition_submission);
grSubmission.query();
while(grSubmission.next){
	if(grSubmission.se_submission_state != "0" && gs.hasRole('x_560219_se.se_user'))//completed
		answer = false;
	if(grSubmission.se_submission_state != "3")
		answer = false;
}
