sys_ui_action.script:
var scriptSubmission = new serverSubmission(); 

if(scriptSubmission.checkUserHasNoInProgressSubmission()){
	//if user doesn't have an 'in-progress' record.
	var grSubmission = new GlideRecord('x_560219_se_submission');
	grSubmission.addQuery('number', current.number);
	grSubmission.query();
	//caculate total score
	var totalScore = 0;
	var grSkillAssessment = new GlideRecord('x_560219_se_skill_assessment');
	grSkillAssessment.addQuery('se_skill_assessment_submission', current.sys_id);
	grSkillAssessment.query();
	while(grSkillAssessment.next()){
		var curScore = grSkillAssessment.getValue('se_skill_assessment_number_proficiency_level');
		if(curScore != null){
			totalScore = totalScore + parseInt(curScore);
		}
	}
	//caculate level for submission
	var minScore = 0, level_sysid = null;
	var grLevel = new GlideRecord('x_560219_se_level');
	grLevel.query();
	while(grLevel.next()){
		score = parseInt(grLevel.se_level_minscore); 
		if(score <= totalScore && score >= minScore){
			minScore = score;
			level_sysid = String(grLevel.sys_id);
		}
	}

	//update submission
	while(grSubmission.next()){
		//change state into submitted and update total score
		grSubmission.se_submission_state = "1";
		grSubmission.se_submission_score = totalScore;
		grSubmission.se_submission_level = level_sysid;
		grSubmission.update();
	}
	gs.eventQueue('x_560219_se.submit_for_review',current,current.number,gs.getUserName());
	gs.addInfoMessage("Submitted successfully");

	//add event to event queue to process
}
//else prevent user submit new record, and notify for user this in-progress record
else{
	gs.addInfoMessage("You have an in-progress submission, please wait for us to process it.");
}
